mapper.select:
select *
from bu_base_alert_accept
where dimension = #{alertAccept.dimension}
  and alert_type = #{alertAccept.alertType}
  and target like '%'||#{alertAccept.target}||'%'

select distinct temp.username
from (
(
select username
from sys_user
WHERE id in
(select target from bu_base_alert_accept
where dimension = 1

    and alert_type = #{alertType}
)
)
UNION ALL
(
select t.username
from sys_user t
left join sys_user_role t1 on t1.user_id = t.id
WHERE t1.role_id in
(select target from bu_base_alert_accept where dimension = 2

    and alert_type = #{alertType}
)
)
UNION ALL
(
select t.username
from sys_user t
left join sys_user_depart t1 on t1.user_id = t.id
WHERE t1.dep_id in
(select target from bu_base_alert_accept where dimension = 3

    and alert_type = #{alertType}
)
)
) temp
