mapper.select:
SELECT t1.*
FROM bu_fault_info_annex t1
WHERE t1.fault_id = #{id}

SELECT
t.*,
t3.group_name AS reportGroupName,
t4.name AS sysName,
t5.name as workstationName,
t1.plan_name
FROM bu_fault_info t
LEFT JOIN bu_repair_plan t1 ON t1.id = t.plan_id
LEFT JOIN bu_repair_program t2 ON t2.id = t1.repair_program_id
LEFT JOIN bu_mtr_workshop_group t3 ON t3.id = t.report_group_id
LEFT JOIN bu_train_asset_type t4 ON t4.id = t.sys_id
LEFT JOIN bu_mtr_workstation t5 ON t5.id = t.work_station_id

    AND t.report_time >= #{queryVO.startDate}
    AND t.report_time <= #{queryVO.endDate}


            AND t.line_id IN (SELECT line_id FROM bu_mtr_depot_line WHERE depot_id = #{queryVO.depotId})



        AND t.line_id = #{queryVO.lineId}


        AND t.train_no = #{queryVO.trainNo}


        AND t2.pro_type = #{queryVO.programType}


        AND t.from_type = #{queryVO.fromType}


ORDER BY t.report_time DESC

SELECT
t.*,
t3.asset_name AS faultAssetName,
t4.name as workstationName,
t5.plan_name
FROM bu_fault_info t
LEFT JOIN bu_repair_plan t1 ON t1.id = t.plan_id
LEFT JOIN bu_repair_program t2 ON t2.id = t1.repair_program_id
LEFT JOIN bu_train_asset t3 ON t3.id = t.fault_asset_id
LEFT JOIN bu_mtr_workstation t4 ON t4.id = t.work_station_id
LEFT JOIN bu_repair_plan t5 ON t5.id = t.plan_id

    AND t.outsource = 1
    AND t.report_time >= #{queryVO.startDate}
    AND t.report_time <= #{queryVO.endDate}


            AND t.line_id IN (SELECT line_id FROM bu_mtr_depot_line WHERE depot_id = #{queryVO.depotId})



        AND t.line_id = #{queryVO.lineId}


        AND t.train_no = #{queryVO.trainNo}


        AND t2.pro_type = #{queryVO.programType}


        AND t.from_type = #{queryVO.fromType}


ORDER BY t.report_time DESC
