mapper.select:
SELECT
t.*,
t2.group_name AS groupName,
(SELECT COUNT(id) FROM bu_work_order_task WHERE order_id = t.id) AS taskCount,
(SELECT COUNT(id) FROM bu_work_order_task WHERE order_id = t.id AND task_status = 2) AS finishedTaskCount
FROM bu_work_order t
LEFT JOIN bu_mtr_workshop t1 ON t1.id = t.workshop_id
LEFT JOIN bu_mtr_workshop_group t2 ON t2.id = t.group_id

    AND t.group_id = #{queryVO.groupId}

        AND t1.depot_id = #{queryVO.depotId}


        AND t.line_id = #{queryVO.lineId}


        AND t.train_no = #{queryVO.trainNo}


        AND t.start_time >= #{queryVO.startDate}


        AND t.finish_time <= #{queryVO.endDate}


order by t.order_code desc

SELECT
t.*,
(SELECT COUNT(id) FROM bu_work_order_task WHERE order_id = t.id) AS taskCount,
(SELECT COUNT(id) FROM bu_work_order_task WHERE order_id = t.id AND task_status = 2) AS finishedTaskCount
FROM bu_work_order t
LEFT JOIN bu_mtr_workshop t1 ON t1.id = t.workshop_id

    AND t.id IN (SELECT order_id from bu_work_order_task WHERE out_task != 0)

        AND t1.depot_id = #{queryVO.depotId}


        AND t.line_id = #{queryVO.lineId}


        AND t.train_no = #{queryVO.trainNo}


        AND t.start_time >= #{queryVO.startDate}


        AND t.finish_time <= #{queryVO.endDate}


order by t.order_code

SELECT
t.*,
t1.start_time as startTime,
t1.finish_time as finishTime
FROM bu_work_order_task t
left join bu_work_order t1 on t1.id = t.order_id
LEFT JOIN bu_mtr_workshop t2 ON t2.id = t1.workshop_id
LEFT JOIN bu_train_asset_type t3 on t3.id=t.asset_type_id

    AND t.out_task != 0

        AND t2.depot_id = #{queryVO.depotId}


        AND t1.line_id = #{queryVO.lineId}


        AND t1.train_no = #{queryVO.trainNo}


        AND t1.start_time >= #{queryVO.startDate}


        AND t1.finish_time <= #{queryVO.endDate}


order by t3.code,t.out_task
