mapper.select:
SELECT t.*, t1.name, t1.asset_code, t1.status, t1.line_id, t2.line_name, t1.workshop_id, t3.name as workshopName, t1.manuf_no, t1.leave_factory, t1.use_date, t1.group_id, t4.depart_name as groupName, t1.duty_user_id, t5.realname as dutyUserName FROM bu_spec_asset_plan t LEFT JOIN bu_spec_assets t1 ON t1.id = t.spec_asset_id left join bu_mtr_line t2 on t2.line_id = t1.line_id left join bu_mtr_workshop t3 on t3.id = t1.workshop_id left join sys_depart t4 on t4.id = t1.group_id left join sys_user t5 on t5.id = t1.duty_user_id
AND t.plan_type = #{queryVO.planType}
AND ( t1.name LIKE CONCAT('%',#{queryVO.searchText},'%') OR t1.asset_code LIKE CONCAT('%',#{queryVO.searchText},'%') )
AND t1.status = #{queryVO.status}
AND ( (t.start_time BETWEEN #{queryVO.startDate} AND #{queryVO.endDate}) OR (t.end_time BETWEEN #{queryVO.startDate} AND #{queryVO.endDate}) )
ORDER BY t.start_time

SELECT * FROM bu_spec_asset_plan WHERE spec_asset_id = #{checkVO.specAssetId}
AND ( (#{checkVO.startTime} BETWEEN start_time AND end_time) OR (#{checkVO.endTime} BETWEEN start_time AND end_time) )
AND (#{checkVO.nowTime} BETWEEN start_time AND end_time)
