mapper.select:
SELECT t1.*, t2.name AS name, t2.model AS model, t2.status AS status, t2.code AS code, t2.asset_code AS assetCode, t2.entrace_date AS entraceDate, t3.name AS warehouseName, t4.group_name AS groupName FROM bu_tool_plan t1 LEFT JOIN bu_material_tools t2 ON t2.id = t1.tool_id LEFT JOIN bu_mtr_warehouse t3 ON t3.id = t2.warehouse_id LEFT JOIN bu_mtr_workshop_group t4 ON t4.id = t2.group_id
AND t1.plan_type = #{queryVO.planType}
AND t2.name LIKE CONCAT('%',#{queryVO.name},'%')
AND t2.code LIKE CONCAT('%',#{queryVO.code},'%')
AND t2.status = #{queryVO.status}
ORDER BY t1.start_time

SELECT * FROM bu_tool_plan WHERE tool_id = #{checkVO.toolId}
AND ( (#{checkVO.startTime} BETWEEN start_time AND end_time) OR (#{checkVO.endTime} BETWEEN start_time AND end_time) )
AND (#{checkVO.nowTime} BETWEEN start_time AND end_time)
