mapper.select:
SELECT t.title title, t1.amount amount, t1.train_index trainIndex, t1.start_date startDate, t1.finish_date finishDate, t1.STATUS status, t2.line_name lineName, t3.NAME programName FROM bu_repair_plan_year t LEFT JOIN bu_repair_plan_year_detail t1 ON t1.year_plan_id = t.id LEFT JOIN bu_mtr_line t2 ON t2.line_id = t1.line_id LEFT JOIN bu_repair_program t3 ON t3.id = t1.program_id LEFT JOIN wf_biz_status t4 ON t4.business_key = t.id
AND t.id = #{queryVO.id}
ORDER BY t.year,t.first_time
