mapper.select:
SELECT t1.*, t2.name AS warehouseName, t3.group_name AS groupName, t4.name AS supplierName, 1 as amount FROM bu_material_tools t1 LEFT JOIN bu_mtr_warehouse t2 ON t2.id = t1.warehouse_id LEFT JOIN bu_mtr_workshop_group t3 ON t3.id = t1.group_id LEFT JOIN bu_base_supplier t4 ON t4.id = t1.supplier_id
t1.tool_type = 5
AND ( t1.name LIKE '%'||#{queryVO.searchText}||'%' OR t1.code LIKE '%'||#{queryVO.searchText}||'%' )
AND t1.status = #{queryVO.status}
AND t1.group_id = #{queryVO.groupId}
ORDER BY t1.entry_date DESC

select t.tool_id, t1.tool_type, t1.code, t1.name, t.order_id, t2.order_code, t2.order_name, t.order_task_id, t3.task_no as orderTaskNo, t3.task_name as orderTaskName, t2.line_id, t4.line_name, t2.train_no, t3.asset_type_id as sysId, t5.name as sysName, t3.task_start as userDate, t2.group_id, t6.group_name from bu_work_order_tool t left join bu_material_tools t1 on t1.id = t.tool_id left join bu_work_order t2 on t2.id = t.order_id left join bu_work_order_task t3 on t3.id = t.order_task_id left join bu_mtr_line t4 on t4.line_id= t2.line_id left join bu_train_asset_type t5 on t5.id = t3.asset_type_id left join bu_mtr_workshop_group t6 on t6.id = t2.group_id where t.tool_id = #{id} and t3.task_status = 2
and t2.train_no = #{trainNo}
order by t3.task_start desc
