update.foreach:
update bu_material_assign_detail
set price = #{item.price}
where id = #{item.id}

update bu_material_assign_detail
set
ebs_wh_code = #{item.ebsWhCode},
ebs_wh_child_code = #{item.ebsWhChildCode}
where id = #{item.id}

mapper.select:
select sum(ifnull(t.amount, 0))
from bu_material_assign_detail t
where t.apply_detail_id = #{applyDetailId}
