mapper.select:
select t.*, t2.name as repairProgramName, t3.line_name as lineName, t4.group_name as groupName, t5.name as workstationName, t6.name as locationName, t1.name, t1.code, t1.spec, t1.unit, t1.category2 as materialTypeCategory, t7.name as sysName from bu_material_must_list t left join bu_material_type t1 on t.material_type_id = t1.id left join bu_repair_program t2 on t2.id = t.repair_program_id left join bu_mtr_line t3 on t3.line_id = t.line_id left join bu_mtr_workshop_group t4 on t4.id = t.group_id left join bu_mtr_workstation t5 on t5.id = t.workstation_id left join bu_train_structure_detail t6 on t6.id = t.location left join bu_train_asset_type t7 on t7.id = t.sys_id
and t.material_type_id in (select id from bu_material_type where ( code like concat('%',#{queryVO.searchText},'%') or name like concat('%',#{queryVO.searchText},'%')))
and t.line_id = #{queryVO.lineId}
and t.repair_program_id = #{queryVO.repairProgramId}
and t.sys_id = #{queryVO.sysId}
and t.workstation_id = #{queryVO.workstationId}
and t.group_id=#{queryVO.groupId}
and t1.code in
#{materialTypeCode}

select t.*, t2.line_name, t3.name as repairProgramName, t4.name as sysName, t5.station_no as workstationNo, t5.name as workstationName from bu_material_must_list t left join bu_repair_plan t1 on t1.line_id = t.line_id and t1.repair_program_id = t.repair_program_id left join bu_mtr_line t2 on t2.line_id = t.line_id left join bu_repair_program t3 on t3.id = t.repair_program_id left join bu_train_asset_type t4 on t4.id = t.sys_id left join bu_mtr_workstation t5 on t5.id = t.workstation_id left join bu_material_type t6 on t6.id = t.material_type_id where t.status = 1 and t.need_amount > 0 and t1.id = #{queryVO.planId}
and t.group_id = #{queryVO.groupId}
order by t.line_id, t3.code, t.group_id, t.material_type_id

select asset_type_id
FROM bu_train_structure_detail
WHERE id = #{structureId}
