mapper.select:
select t.*, t1.order_code as workOrderCode, t1.order_name as workOrderName, t2.depart_name as groupName, t3.realname as handleUserName from bu_material_returned t left join bu_work_order t1 on t1.id = t.work_order_id left join sys_depart t2 on t2.id = t.group_id left join sys_user t3 on t3.id = t.handle_user_id
and ( t.bill_code like concat('%',#{queryVO.searchText},'%') or t.bill_name like concat('%',#{queryVO.searchText},'%') )
and t.bill_date >= #{queryVO.startDate}
and t.bill_date <= #{queryVO.endDate}
and t1.plan_id=#{queryVO.planId}
order by t.bill_code desc

select group_id
from bu_work_order
where id = #{workOrderId}
