if.foreach:
update bu_material_group_stock
set
amount = #{item.amount}
where id = #{item.id}

mapper.select:
SELECT t.*, t3.depart_name as groupName, t1.code AS materialTypeCode, t1.name AS materialTypeName, t1.price AS materialTypePrice, t2.wbs AS warehouseWbs, t2.name AS warehouseName, t1.category1 AS materialType, t1.category2 AS materialAttr, t1.unit AS unit, t1.spec AS spec, t4.name as systemName, t5.name as workstationName, t6.can_replace FROM bu_material_group_stock t LEFT JOIN bu_material_type t1 ON t1.id = t.material_type_id LEFT JOIN bu_mtr_warehouse t2 on t2.id = t.warehouse_id left join sys_depart t3 on t3.id = t.group_id left join bu_train_asset_type t4 on t4.id = t.system_id left join bu_mtr_workstation t5 on t5.id = t.workstation_id left join bu_material_type_replace t6 on t6.id = t.material_type_id where t.amount != 0
and t.material_type_id in
#{materialTypeId}
and t.group_id = #{queryVO.groupId}
and t.train_no = #{queryVO.trainNo}
AND t1.category1 = #{queryVO.materialType}
AND t1.category2 = #{queryVO.materialAttr}
order by t.group_id, t.train_no, t.material_type_id, t.trade_no, t2.code, t.amount
