mapper.select:
SELECT t.*, t1.entry_no, t2.code AS materialTypeCode, t2.name AS materialTypeName, t2.spec AS materialTypeSpec, t2.unit AS materialTypeUnit, t2.category1 AS materialTypeCategory1, t3.name AS selfWarehouseName, t4.realname as entryUserName, t1.entry_dept_id, (select a.name from bu_mtr_warehouse a where a.wh_level = '3' and (a.code = t.ebs_warehouse_levl_2 or a.id=t.ebs_warehouse_levl_2)) as selfWarehouseName2, t5.depart_name as entryDeptName, t6.realname as confirmUserName FROM bu_material_entry_detail t LEFT JOIN bu_material_entry_order t1 on t1.id = t.entry_order_id LEFT JOIN bu_material_type t2 ON t2.id = t.material_type_id LEFT JOIN bu_mtr_warehouse t3 ON t3.id = t.self_warehouse_id LEFT JOIN sys_user t4 ON t4.id = t.entry_user_id LEFT JOIN sys_depart t5 ON t5.id = t1.entry_dept_id LEFT JOIN sys_user t6 ON t6.id = t.confirm_user_id
AND t3.wbs like (select wbs from bu_mtr_warehouse where id = #{queryVO.warehouseId})||'%'
AND t.entry_class = #{queryVO.entryClass}
AND t2.category1 = #{queryVO.materialCategory}
AND ( t2.code LIKE '%'||#{queryVO.searchText}||'%' OR t2.name LIKE '%'||#{queryVO.searchText}||'%' )
AND t.confirm = #{queryVO.confirm}
order by t1.entry_no desc, t2.code, t.entry_date desc
