mapper.select:
select * from ( SELECT t1.*, t2.name AS warehouseName, case when t1.GROUP_ID='CJ' then '架大修车间' else t3.group_name end as groupName, 1 as amount, t4.name as supplierName, t5.spec, t5.unit, t5.kind, t5.category1, t5.category2, t6.realname as userName, NVL2(t1.expir_date, (case when (SYSDATE > t1.expir_date) then 1 else 0 end), 0) as overdue FROM bu_material_tools t1 LEFT JOIN bu_mtr_warehouse t2 ON t2.id = t1.warehouse_id LEFT JOIN bu_mtr_workshop_group t3 ON t3.id = t1.group_id LEFT JOIN bu_base_supplier t4 ON t4.id = t1.supplier_id LEFT JOIN bu_material_type t5 ON t5.id = t1.material_type_id LEFT JOIN sys_user t6 on t6.id = t1.user_id
AND ( t1.name LIKE '%'||#{queryVO.searchText}||'%' OR t1.code LIKE '%'||#{queryVO.searchText}||'%' OR t1.asset_code LIKE '%'||#{queryVO.searchText}||'%' )
AND t1.tool_type = #{queryVO.toolType}
AND t1.status = #{queryVO.status}
AND t1.group_id = #{queryVO.groupId}
AND t1.material_type_id = #{queryVO.materialTypeId}
AND t1.next_check_time < #{queryVO.nextCheckTime}
) ORDER BY overdue DESC, next_check_time, asset_code

SELECT *
FROM bu_material_tools
WHERE material_type_id = #{toolTypeId}
  AND group_id = #{workGroupId}
  AND status != 3
ORDER BY status, next_check_time DESC

