mapper.update:
update bu_contract_pay
set leftover=leftover + #{subtract, jdbcType=NUMERIC}
where id = #{id, jdbcType=VARCHAR}

update bu_contract_pay
set left_qa_money=left_qa_money + #{subtract, jdbcType=NUMERIC}
where id = #{id, jdbcType=VARCHAR}

mapper.select:
select t.* ,t1.contract_name as contractName,t1.contract_no as  contractNo,
       t2.name as supplier,t3.line_name as lineName
from bu_contract_pay t
   left join bu_contract_info t1 on t1.id = t.contract_id
   left join bu_base_supplier t2 on t1.supplier_id =t2.id
   left join bu_mtr_line t3 on t3.line_id =t1.line_id


        t.id=#{pay.id}


    and t.contract_id=#{pay.contractId}



        and t.pay_desc like '%'||#{pay.searchText}||'%'


        and t3.line_id =#{pay.lineId}


        and t1.supplier_id=#{pay.supplierId}


        and t.pay_date between #{pay.startTime} AND #{pay.endTime}


order by t.pay_no
