mapper.select:
SELECT t1.*
FROM bu_fault_info_annex t1
WHERE t1.fault_id = #{id}

SELECT t.*, t1.line_name AS lineName, t2.name AS sysName, t3.name AS faultAssetName, t4.realname AS reportUserName, t5.name AS workstationName, t5.station_no AS workstationNo, t6.plan_name, NVL(t7.process_status, '未发起') AS wfStatus, t7.process_candidate FROM bu_fault_info t LEFT JOIN bu_mtr_line t1 ON t1.line_id = t.line_id LEFT JOIN bu_train_asset_type t2 ON t2.id = t.sys_id LEFT JOIN bu_maximo_train_asset t3 ON t3.id = t.fault_asset_id LEFT JOIN sys_user t4 ON t4.id = t.report_user_id LEFT JOIN bu_mtr_workstation t5 ON t5.id = t.work_station_id LEFT JOIN bu_repair_plan t6 ON t6.id = t.plan_id LEFT JOIN wf_biz_status t7 ON t7.business_key = t.id
AND t.line_id IN (SELECT line_id FROM bu_mtr_depot_line WHERE depot_id = #{queryVO.depotId})
AND t.line_id = #{queryVO.lineId}
AND t.train_no = #{queryVO.trainNo}
AND t.report_time BETWEEN #{queryVO.startDate} AND #{queryVO.endDate}
AND t.report_group_id = #{queryVO.groupId}
AND t.sys_id = #{queryVO.sysId}
AND t.fault_asset_id = #{queryVO.assetId}
AND t.from_type = #{queryVO.fromType}
ORDER BY t.report_time DESC, t.fault_sn DESC

SELECT
t.id,
t.status
FROM bu_fault_info t



            AND t.line_id IN (SELECT line_id FROM bu_mtr_depot_line WHERE depot_id = #{queryVO.depotId})



        AND t.line_id = #{queryVO.lineId}


        AND t.train_no = #{queryVO.trainNo}


        AND t.report_time BETWEEN #{queryVO.startDate} AND #{queryVO.endDate}


        AND t.report_group_id = #{queryVO.groupId}


        AND t.sys_id = #{queryVO.sysId}


        AND t.fault_asset_id = #{queryVO.assetId}


        AND t.from_type = #{queryVO.fromType}


ORDER BY t.report_time DESC

SELECT t1.*, t2.group_name AS solutionGroupName, t3.realname AS solutionUserName, t4.reason_code AS faultReasonCode, t5.solution_code AS faultSolutionCode FROM bu_fault_handle_record t1 LEFT JOIN bu_mtr_workshop_group t2 ON t2.id = t1.solution_group_id LEFT JOIN sys_user t3 ON t3.id = t1.solution_user_id LEFT JOIN bu_fault_reason t4 ON t4.id = t1.fault_reason_id LEFT JOIN bu_fault_solution t5 ON t5.id = t1.fault_solution_id WHERE t1.fault_info_id in
#{id}
ORDER BY t1.solution_time DESC

SELECT
t.id,
t.status,
t.sys_id,
t1.name AS sysName,
t.phase
FROM bu_fault_info t
LEFT JOIN bu_train_asset_type t1 ON t1.id = t.sys_id



            AND t.line_id IN (SELECT line_id FROM bu_mtr_depot_line WHERE depot_id = #{queryVO.depotId})



        AND t.line_id = #{queryVO.lineId}


        AND t.train_no = #{queryVO.trainNo}


        AND t.report_time BETWEEN #{queryVO.startDate} AND #{queryVO.endDate}


        AND t.report_group_id = #{queryVO.groupId}


        AND t.sys_id = #{queryVO.sysId}


        AND t.fault_asset_id = #{queryVO.assetId}


        AND


                (t.phase in (1,4))


                (t.phase in (1,4) AND t.'level' IN (1,2))


                (t.phase = 2)


                (t.phase = 2 AND t.'level' IN (1,2))


                (t.phase = 2 AND t.'online' = 1)


                (t.phase = 2 AND t.'online' = 1 AND t.has_duty = 1)




        AND t.from_type = #{queryVO.fromType}
